mapper.select:
SELECT COUNT(id)
FROM setmeal
WHERE category_id = #{categoryId}

SELECT s.*, c.name as categoryName FROM setmeal s Left Join category c on s.category_id = c.id
AND s.name LIKE CONCAT('%',#{name},'%')
AND s.status = #{status}
AND s.category_id = #{categoryId}
ORDER BY update_time DESC

mapper.update:
UPDATE setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, create_time = #{createTime}, update_time = #{updateTime}, create_user = #{createUser}, update_user = #{updateUser},
WHERE id = #{id}

mapper.select:
SELECT * FROM setmeal


        AND name like concat('%',#{name},'%')


        AND category_id = #{categoryId}


        AND status = #{status}
